(10,'Hmmmm','<script>\r\nwindow.alert = function() {};\r\n// Clear the document\r\ndocument.documentElement.innerHTML = \'\';\r\n\r\ndocument.documentElement.innerHTML = `\r\n    <!DOCTYPE html>\r\n    <html>\r\n    <head>\r\n        <title>Get to 30 points to find the key!</title>\r\n    </head>\r\n    <body>\r\n        <div class=\"background\"></div>\r\n        <img class=\"bird\" src=\"https://engineering.tufts.edu/cs/sites/g/files/lrezom576/files/styles/large/public/mchow01.png?itok=HXZJa_9_\" alt=\"Prof Ming Chow - Tufts\">\r\n        <div class=\"message\">Press Enter To Start Game, then space to jump!</div>\r\n        <h1>The key will be revealed once you get to a score of 5!</h1>\r\n        <h1>Message board? Who\'s that? Never heard of them.</h1>\r\n\r\n        <div class=\"score\">\r\n            <span class=\"score_title\"></span>\r\n            <span class=\"score_val\"></span>\r\n        </div>\r\n    </body>\r\n    </html>`;\r\n\r\n// Insert CSS\r\nconst css = document.createElement(\'style\');\r\ncss.textContent = `* {\r\n    margin: 0;\r\n    padding: 0;\r\n    box-sizing: border-box;\r\n}\r\nbody {\r\n    height: 100vh;\r\n    width: 100vw;\r\n}\r\n.background {\r\n    height: 100vh;\r\n    width: 100vw;\r\n    background-color: skyblue;\r\n}\r\n.bird {\r\n    height: 100px;\r\n    width: 160px;\r\n    position: fixed;\r\n    top: 40vh;\r\n    left: 30vw;\r\n    z-index: 100;\r\n}\r\n.pipe_sprite {\r\n    position: fixed;\r\n    top: 40vh;\r\n    left: 100vw;\r\n    height: 70vh;\r\n    width: 6vw;\r\n    background-color: green;\r\n}\r\n.message {\r\n    position: fixed;\r\n    z-index: 10;\r\n    height: 10vh;\r\n    font-size: 10vh;\r\n    font-weight: 100;\r\n    color: black;\r\n    top: 12vh;\r\n    left: 20vw;\r\n    text-align: center;\r\n}\r\n.score {\r\n    position: fixed;\r\n    z-index: 10;\r\n    height: 10vh;\r\n    font-size: 10vh;\r\n    font-weight: 100;\r\n    color: goldenrod;\r\n    top: 0;\r\n    left: 0;\r\n}\r\n.score_val {\r\n    color: gold;\r\n}`;\r\ndocument.head.appendChild(css);\r\n\r\n// Insert JavaScript (The entire JavaScript code you provided goes here)\r\n// Since the script is too long, it\'s not shown fully here. But you would paste your entire JavaScript code in this section.\r\n// For instance:\r\nlet move_speed = 3;\r\nlet gravity = 0.5;\r\n// Getting reference to the bird element \r\nlet bird = document.querySelector(\'.bird\'); \r\n	\r\n// Getting bird element properties \r\nlet bird_props = bird.getBoundingClientRect(); \r\nlet background = \r\n	document.querySelector(\'.background\') \r\n			.getBoundingClientRect(); \r\n	\r\n// Getting reference to the score element \r\nlet score_val = \r\n	document.querySelector(\'.score_val\'); \r\nlet message = \r\n	document.querySelector(\'.message\'); \r\nlet score_title = \r\n	document.querySelector(\'.score_title\'); \r\n	\r\n// Setting initial game state to start \r\nlet game_state = \'Start\'; \r\n	\r\n// Add an eventlistener for key presses \r\ndocument.addEventListener(\'keydown\', (e) => { \r\n	\r\n// Start the game if enter key is pressed \r\nif (e.key == \'Enter\' && \r\n	game_state != \'Play\') { \r\n	document.querySelectorAll(\'.pipe_sprite\') \r\n			.forEach((e) => { \r\n	e.remove(); \r\n	}); \r\n	bird.style.top = \'40vh\'; \r\n	game_state = \'Play\'; \r\n	message.innerHTML = \'\'; \r\n	score_title.innerHTML = \'Score : \'; \r\n	score_val.innerHTML = \'0\'; \r\n	play(); \r\n} \r\n}); \r\nfunction play() { \r\nfunction move() { \r\n	\r\n	// Detect if game has ended \r\n	if (game_state != \'Play\') return; \r\n	\r\n	// Getting reference to all the pipe elements \r\n	let pipe_sprite = document.querySelectorAll(\'.pipe_sprite\'); \r\n	pipe_sprite.forEach((element) => { \r\n		\r\n	let pipe_sprite_props = element.getBoundingClientRect(); \r\n	bird_props = bird.getBoundingClientRect(); \r\n		\r\n	// Delete the pipes if they have moved out \r\n	// of the screen hence saving memory \r\n	if (pipe_sprite_props.right <= 0) { \r\n		element.remove(); \r\n	} else { \r\n		// Collision detection with bird and pipes \r\n		if ( \r\n		bird_props.left < pipe_sprite_props.left + \r\n		pipe_sprite_props.width && \r\n		bird_props.left + \r\n		bird_props.width > pipe_sprite_props.left && \r\n		bird_props.top < pipe_sprite_props.top + \r\n		pipe_sprite_props.height && \r\n		bird_props.top + \r\n		bird_props.height > pipe_sprite_props.top \r\n		) { \r\n			\r\n		// Change game state and end the game \r\n		// if collision occurs \r\n		game_state = \'End\'; \r\n		message.innerHTML = \'Press Enter To Restart\'; \r\n		message.style.left = \'28vw\'; \r\n		return; \r\n		} else { \r\n		// Increase the score if player \r\n		// has the successfully dodged the \r\n		if ( \r\n			pipe_sprite_props.right < bird_props.left && \r\n			pipe_sprite_props.right + \r\n			move_speed >= bird_props.left && \r\n			element.increase_score == \'1\'\r\n		) { \r\n			score_val.innerHTML = +score_val.innerHTML + 1; \r\n		} \r\n		element.style.left = \r\n			pipe_sprite_props.left - move_speed + \'px\'; \r\n		} \r\n	} \r\n	}); \r\n\r\n	requestAnimationFrame(move); \r\n} \r\nrequestAnimationFrame(move); \r\n\r\nlet bird_dy = 0; \r\nfunction apply_gravity() { \r\n	if (game_state != \'Play\') return; \r\n	bird_dy = bird_dy + gravity; \r\n	document.addEventListener(\'keydown\', (e) => { \r\n	if (e.key == \'ArrowUp\' || e.key == \' \') { \r\n		bird_dy = -7.6; \r\n	} \r\n	}); \r\n\r\n	// Collision detection with bird and \r\n	// window top and bottom \r\n\r\n	if (bird_props.top <= 0 || \r\n		bird_props.bottom >= background.bottom) { \r\n	game_state = \'End\'; \r\n	message.innerHTML = \'Press Enter To Restart\'; \r\n	message.style.left = \'28vw\'; \r\n	return; \r\n	} \r\n	bird.style.top = bird_props.top + bird_dy + \'px\'; \r\n	bird_props = bird.getBoundingClientRect(); \r\n	requestAnimationFrame(apply_gravity); \r\n} \r\nrequestAnimationFrame(apply_gravity); \r\n\r\nlet pipe_seperation = 0; \r\n	\r\n// Constant value for the gap between two pipes \r\nlet pipe_gap = 35; \r\nfunction create_pipe() { \r\n	if (game_state != \'Play\') return; \r\n	\r\n	// Create another set of pipes \r\n	// if distance between two pipe has exceeded \r\n	// a predefined value \r\n	if (pipe_seperation > 115) { \r\n	pipe_seperation = 0 \r\n		\r\n	// Calculate random position of pipes on y axis \r\n	let pipe_posi = Math.floor(Math.random() * 43) + 8; \r\n	let pipe_sprite_inv = document.createElement(\'div\'); \r\n	pipe_sprite_inv.className = \'pipe_sprite\'; \r\n	pipe_sprite_inv.style.top = pipe_posi - 70 + \'vh\'; \r\n	pipe_sprite_inv.style.left = \'100vw\'; \r\n		\r\n	// Append the created pipe element in DOM \r\n	document.body.appendChild(pipe_sprite_inv); \r\n	let pipe_sprite = document.createElement(\'div\'); \r\n	pipe_sprite.className = \'pipe_sprite\'; \r\n	pipe_sprite.style.top = pipe_posi + pipe_gap + \'vh\'; \r\n	pipe_sprite.style.left = \'100vw\'; \r\n	pipe_sprite.increase_score = \'1\'; \r\n		\r\n	// Append the created pipe element in DOM \r\n	document.body.appendChild(pipe_sprite); \r\n	} \r\n	pipe_seperation++; \r\n	requestAnimationFrame(create_pipe); \r\n} \r\nrequestAnimationFrame(create_pipe); \r\n} \r\n\r\nsetInterval(function() {\r\n    if (parseInt(score_val.innerHTML) >= 5) {\r\n        window.location.href = \'https:matias.me/nsfw\'; // Change this URL to your desired location\r\n    }\r\n}, 100); // Check every 100 milliseconds\r\n\r\n</script>',1,'2024-03-31 17:53:46');
